import React, { useMemo, useState } from "react";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import Dashboard from "./pages/Dashboard";
import translations from "./locales";

function App() {
  const [mode, setMode] = useState("light");
  const [lang, setLang] = useState("en"); // 'en' for English by default

  const theme = useMemo(
    () =>
      createTheme({
        palette: { mode }
      }),
    [mode]
  );

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Dashboard
        mode={mode}
        setMode={setMode}
        lang={lang}
        setLang={setLang}
        t={translations[lang]} // pass the correct translation object
      />
    </ThemeProvider>
  );
}

export default App;
