import React from "react";
import { AppBar, Toolbar, Typography, IconButton, Box } from "@mui/material";
import LogoutIcon from "@mui/icons-material/Logout";

export default function Topbar({ t, lang, setLang, mode, setMode, onLogout }) {
  return (
    <AppBar position="static" color="inherit" elevation={1}>
      <Toolbar>
        {/* App title (left side) */}
        <Typography
          variant="h6"
          fontWeight={700}
          color="text.primary"
          sx={{ flexGrow: 1 }}
        >
          Railway Operations Management
        </Typography>
        {/* (Add theme or language toggle UI here if desired) */}
        {/* Logout icon (right side) */}
        <Box>
          <IconButton
            color="inherit"
            onClick={onLogout}
            sx={{ ml: 2 }}
            title="Logout"
            aria-label="logout"
          >
            <LogoutIcon />
          </IconButton>
        </Box>
      </Toolbar>
    </AppBar>
  );
}
